from pipe.am.project import Project
node = kwargs.get('node', None)
if node:
	if node.type().definition() is not None:
		node_path = node.path()
		node_name = node.type().name()
		if str(node_name) == "dcc_camera":
			return 0
		index = node_name.rfind('_')
		node_name = node_name[:index]
		if not str(node_name) == "dcc":
			return 0

		name = node.parm("asset_name")
		if name:
			name = name.evalAsString()
		else:
			return 0
		try:
			node_body = Project().get_body(name)
		except:
			return 0
		if node_body:
			if node_body.is_asset():
				return 1
return 0

scriptItem.scriptCode:
from pipe.am.project import Project from pipe.tools.houtools.publisher import publisher from pipe.am.body import AssetType node = kwargs.get('node', None) if node: import hou publisher = publisher.Publisher() hou.session.publisher = publisher
name = node.parm("asset_name").evalAsString() node_body = Project().get_body(name)
if node_body.get_type() == AssetType.SET: publisher.publish_set(node=node, name=name) else: publisher.publish_asset(node=node, name=name, inner=False)

